DROP TABLE IF EXISTS `fa_clist`;
/*!40101 SET @saved_cs_client     = @@character_set_client */;
/*!40101 SET character_set_client = utf8 */;
CREATE TABLE `fa_clist` (
    `id` int(10) NOT NULL AUTO_INCREMENT COMMENT 'id',
    `title` varchar(200) NOT NULL COMMENT '标题',
    `ca_id` int(10) NOT NULL COMMENT '栏目ID',
    `author` varchar(50) DEFAULT NULL COMMENT '作者',
    `avatar` varchar(100) DEFAULT NULL COMMENT '头像',
    `image` varchar(200) DEFAULT NULL COMMENT '缩略图',
    `content` text COMMENT '活动详情',
    `url_type` varchar(10) DEFAULT '' COMMENT '跳转类型',
    `wxappid` varchar(50) DEFAULT '' COMMENT '小程序APPID',
    `url` text COMMENT '链接',
    `images` varchar(500) DEFAULT NULL COMMENT '组图',
    `videofile` varchar(300) DEFAULT NULL COMMENT '视频',
    `switch` tinyint(1) NOT NULL DEFAULT '0' COMMENT '首页推荐',
    `description` varchar(255) DEFAULT NULL COMMENT '描述',
    `createtime` bigint(16) DEFAULT NULL COMMENT '创建时间',
    `updatetime` bigint(16) DEFAULT NULL COMMENT '更新时间',
    `weigh` int(10) NOT NULL DEFAULT '0' COMMENT '权重',
    PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=42 DEFAULT CHARSET=utf8 COMMENT='活动列表';
/*!40101 SET character_set_client = @saved_cs_client */;

