DROP TABLE IF EXISTS `fa_glist`;
/*!40101 SET @saved_cs_client     = @@character_set_client */;
/*!40101 SET character_set_client = utf8 */;
CREATE TABLE `fa_glist` (
    `id` int(10) NOT NULL AUTO_INCREMENT COMMENT 'id',
    `title` varchar(200) NOT NULL COMMENT '标题',
    `fid` int(10) NOT NULL COMMENT '栏目ID',
    `image` varchar(200) DEFAULT NULL COMMENT '缩略图',
    `author` varchar(50) DEFAULT NULL COMMENT '作者',
    `avatar` varchar(200) DEFAULT NULL COMMENT '头像',
    `url_type` varchar(10) DEFAULT '' COMMENT '跳转方式',
    `wxappid` varchar(50) DEFAULT '' COMMENT '跳转APPID',
    `url` text COMMENT '跳转链接',
    `description` varchar(255) DEFAULT NULL COMMENT '描述',
    `hid` int(6) DEFAULT NULL COMMENT '活动ID',
    `gid` int(6) DEFAULT NULL COMMENT '产品ID',
    `createtime` bigint(16) DEFAULT NULL COMMENT '创建时间',
    `updatetime` bigint(16) DEFAULT NULL COMMENT '更新时间',
    `weigh` int(10) NOT NULL DEFAULT '0' COMMENT '权重',
    `status` enum('normal','hidden') DEFAULT 'normal' COMMENT '状态',
    PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=22 DEFAULT CHARSET=utf8 COMMENT='活动列表';
/*!40101 SET character_set_client = @saved_cs_client */;

