DROP TABLE IF EXISTS `fa_goods`;
/*!40101 SET @saved_cs_client     = @@character_set_client */;
/*!40101 SET character_set_client = utf8 */;
CREATE TABLE `fa_goods` (
        `id` int(10) NOT NULL AUTO_INCREMENT COMMENT 'id',
        `title` varchar(200) NOT NULL COMMENT '标题',
        `name` varchar(100) DEFAULT '' COMMENT '产品名称',
        `sc_id` int(10) NOT NULL COMMENT '栏目',
        `author` varchar(50) DEFAULT '' COMMENT '作者',
        `avatar` varchar(100) DEFAULT '' COMMENT '头像',
        `image` varchar(200) DEFAULT NULL COMMENT '缩略图',
        `images` varchar(255) DEFAULT NULL COMMENT '组图',
        `content` text COMMENT '产品详情',
        `description` varchar(255) DEFAULT NULL COMMENT '描述',
        `wxappid` varchar(50) DEFAULT '' COMMENT '跳转的APPID',
        `url` varchar(200) DEFAULT '' COMMENT '跳转链接',
        `cjswitch` tinyint(1) NOT NULL DEFAULT '0' COMMENT '场景推荐',
        `txswitch` tinyint(1) NOT NULL DEFAULT '0' COMMENT '套系产品',
        `cpswitch` tinyint(1) NOT NULL DEFAULT '0' COMMENT '产品攻略',
        `createtime` bigint(16) DEFAULT NULL COMMENT '创建时间',
        `file` varchar(200) DEFAULT NULL COMMENT '文件',
        `updatetime` bigint(16) DEFAULT NULL COMMENT '更新时间',
        `weigh` int(10) NOT NULL DEFAULT '0' COMMENT '权重',
        `status` enum('normal','hidden') NOT NULL DEFAULT 'normal' COMMENT '状态',
        PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=90 DEFAULT CHARSET=utf8 COMMENT='产品列表';
/*!40101 SET character_set_client = @saved_cs_client */;

LOCK TABLES `fa_goods` WRITE;
